import React, { useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faSquareWhatsapp,
  faSquareInstagram,
  faSquareFacebook,
  faSquareGithub,
} from '@fortawesome/free-brands-svg-icons';
import { faEnvelope } from '@fortawesome/free-solid-svg-icons';

function FooterComponent() {
  const socialLinks = [
    {
      icon: faSquareWhatsapp,
      href: "https://wa.me/393396446669",
    },
    {
      icon: faSquareInstagram,
      href: "https://www.instagram.com/matteomigliarese_/",
    },
    {
      icon: faSquareFacebook,
      href: "https://www.facebook.com/matteo.migliarese.14",
    },
    {
      icon: faSquareGithub,
      href: "https://github.com/migliaresematteo",
    },
  ];

  useEffect(() => {
    const script = document.createElement('script');
    script.src = "//embed.typeform.com/next/embed.js";
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <footer className="py-5">
      <Container>
        <div className="row px-0 my-5">
          <div className="col-12 mx-3 mx-md-0 col-md-6 pe-0 pe-md-5 ps-0 my-5">
            <h6 className="display-6 fw-bolder underline w-max">Contacts</h6>
            <p className="lead my-3 my-md-0 mt-md-3 mb-md-4">
              Feel free to contact me if you need assistance, collaboration or
              anything else! Please use the following contacts for professional
              purposes only and use social media for informal matters.
            </p>
            <p className='lead mb-3 mb-md-0'><FontAwesomeIcon icon={faEnvelope} /> migliaresematteo@gmail.com</p>
          </div>
          <div className="col-12 mx-3 mx-md-0 col-md-6 ps-0 ps-md-5 my-5">
            <h6 className="display-6 fw-bolder underline w-max">Socials</h6>
            <p className="lead my-3">
              Get in touch with me and follow me on my socials â€“ my latest adventure is just getting started, and you don't want to miss a thing! I'll be posting live updates, stories, and exclusive content.
            </p>
            <div className="d-flex">
              {socialLinks.map((link) => (
                <a
                  key={link.href}
                  className="pe-3 text-decoration-none text-dark display-6"
                  href={link.href}
                  target='_blank'
                >
                  <FontAwesomeIcon icon={link.icon} />
                </a>
              ))}
            </div>
          </div>
          <h6 className="display-6 fw-bolder underline w-max">Contact form</h6>
        <p className="lead mt-3 mb-4">
              If you need to contact me rapidly for business matters you can compile the form down there, i'll respond as soon as possible!
            </p>
        <div data-tf-live="01HV72Y3S1WQTHJ1GBHTHVQPC1"></div>
        </div>
      </Container>
    </footer>
  );
}

export default FooterComponent;
