import React from 'react';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import frame3 from '../assets/frame3.png';
import frame1 from '../assets/frame1.png';
import frame2 from '../assets/frame2.png';

function ECommerceSection() {
    const cards = [
        {
            title: "Customised website",
            text: "Complete customisation Unlimited functionality Maintenance included for the first year ",
            image: frame3,
        },
        {
            title: "SEO Analysis",
            text: "Some quick example text to build on the card title and make up the bulk of the card's content.",
            image: frame2,
        },
        {
            title: "UI/UX Analysis",
            text: "Some quick example text to build on the card title and make up the bulk of the card's content.",
            image: frame1,
        }
    ];

    return (
        <Container>
            <h6 className="px-0 display-6 fw-bolder underline w-max mb-3">Shop</h6>
            <p className="lead mt-3 mb-4 px-0">
                Below you can find a selection of the many services I offer, feel free to contact me about any of them by pressing the related button.
            </p>
            <div className='row no-gutters'>
                {cards.map((card, index) => (
                    <div key={index} className='col-12 col-md-4'>
                        <Card>
                            <Card.Img variant="top" src={card.image} />
                            <Card.Body>
                                <Card.Title>{card.title}</Card.Title>
                                <ul>
                                    <li>Complete customisation</li>
                                    <li>Unlimited functionality</li>
                                    <li>Maintenance included for the first year</li>
                                    <li>Delivery in 2 months</li>
                                </ul>
                                <a className='d-block w-max bg-dark text-light fw-bolder rounded-2 text-light py-2 mt-3 px-3 text-decoration-none'>Contact me</a>
                            </Card.Body>
                        </Card>
                    </div>
                ))}
            </div>
        </Container>
    );
}

export default ECommerceSection;
