import Container from 'react-bootstrap/Container';

function HeaderComponent({content}) {
    return (
        <header id="home" className="pageHeader d-flex flex-column justify-content-center">
            <Container>
                {content.map((index) => (
                    <h1 key={index} className='text-light display-1 fw-bold'>{index.title}</h1>
                ))}
                {content.map((index) => (
                    <p key={index} className='text-light lead fw-bold mb-4'>{index.subtitle}</p>
                ))}
                {content.map((index) => (
                    <a key={index} className='w-max bg-light text-dark fw-bolder rounded-2 text-light py-2 px-3 text-decoration-none' href={index.href}>{index.cta}</a>
                ))}
            </Container>
        </header>
    );
}

export default HeaderComponent;
