import React, { useEffect } from 'react';
import Container from 'react-bootstrap/Container';

function HeaderComponent({ content }) {
    useEffect(() => {
        const handleScroll = () => {
            const scrollTop = window.pageYOffset;
            const header = document.querySelector("#home");
            const { top: t, height: h } = header.getBoundingClientRect();
            const centerY = t + h / 2;
            header.style.setProperty('--posY', centerY - scrollTop);
        };

        const isMobile = window.innerWidth < 768;

        if (isMobile) {
            window.addEventListener("scroll", handleScroll);
        } else {
            const handlePointerMove = (e) => {
                const { currentTarget: el, clientX: x, clientY: y } = e;
                const { top: t, left: l, width: w, height: h } = el.getBoundingClientRect();
                el.style.setProperty('--posX',  x-l-w/2);
                el.style.setProperty('--posY',  y-t-h/2);
            };

            document.querySelector("#home").addEventListener("pointermove", handlePointerMove);

            return () => {
                document.querySelector("#home").removeEventListener("pointermove", handlePointerMove);
            };
        }

        return () => {
            if (isMobile) {
                window.removeEventListener("scroll", handleScroll);
            }
        };
    }, []);

    return (
        <header id="home" className="pageHeader d-flex flex-column justify-content-center" style={{ position: 'relative' }}>
            <Container>
                {content.map((index) => (
                    <h1 key={index} className='text-light display-1 fw-bold'>{index.title}</h1>
                ))}
                {content.map((index) => (
                    <p key={index} className='text-light lead fw-bold mb-4'>{index.subtitle}</p>
                ))}
                {content.map((index) => (
                    <a key={index} className='w-max bg-light text-dark fw-bolder rounded-2 text-light py-2 px-3 text-decoration-none' href={index.href}>{index.cta}</a>
                ))}
            </Container>
        </header>
    );
}

export default HeaderComponent;
