import React from 'react';
import { Container, Row, Col, Card, Button, Badge } from 'react-bootstrap';

const products = [
  {
    id: 1,
    title: "Product 1",
    subtitle: "Subtitle 1",
    description: "Description of Product 1.",
    image: "https://via.placeholder.com/350x150",
    available: true
  },
  {
    id: 2,
    title: "Product 2",
    subtitle: "Subtitle 2",
    description: "Description of Product 2.",
    image: "https://via.placeholder.com/400x400",
    available: false
  }
];

const ECommerceSection = () => {
  // Calculate the height for the images dynamically
  const imageHeight = () => {
    // Assuming all images have the same aspect ratio (width:height)
    // Calculate the height based on the width (here, I've set it to 200px)
    return "200px";
  };

  return (
    <Container>
      <Row>
        {products.map(product => (
          <Col key={product.id}>
            <Card>
              {/* Set a style for the image with the calculated height */}
              <Card.Img variant="top" src={product.image} style={{ height: imageHeight() }} />
              <Card.Body>
                <Card.Title>{product.title}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{product.subtitle}</Card.Subtitle>
                <Card.Text>{product.description}</Card.Text>
                {product.available ? (
                  <Badge variant="success">Available</Badge>
                ) : (
                  <Badge variant="danger">Out of Stock</Badge>
                )}
                <Button variant="primary" className="ml-2" disabled={!product.available}>Buy</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default ECommerceSection;
