import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Certificates() {
    const certificates = [
        { title: "CISCO CCNAv7", subtitle: "Introduction to Networks", description: "Learn the basics of networking including OSI model, TCP/IP, subnetting, and more." },
        { title: "CISCO IoT", subtitle: "Introduction to IoT", description: "Explore the fundamentals of the Internet of Things including IoT devices, protocols, and applications." },
        { title: "CISCO CyberSecurity", subtitle: "Introduction to Cybersecurity", description: "Understand key cybersecurity concepts such as threats, vulnerabilities, cryptography, and network security." },
        { title: "HackersGen", subtitle: "Complete Linux/Ubuntu course", description: "Master Linux/Ubuntu operating system, command line, file system, and shell scripting." },
    ];

    return (
        <Container>
            <Row id='certifications' className="px-2 my-5">
            <h6 className="px-0 mx-2 display-6 fw-bolder underline w-max mb-4">My certifications</h6>
            <p className='px-0 mx-2 mb-4 lead'>Over the years, I have taken several exams aimed at earning useful certifications in different fields and you can find some of them below!</p>
            {certificates.map((certificate) => (
                <Col key={certificate.id} lg={3} md={6} className="mb-3 px-2">
                    <Card style={{ height: "100%" }}>
                        <div className='bg-dark text-light py-2 px-3 fw-bold'>{certificate.title}</div>
                        <Card.Body>
                            <Card.Title>{certificate.subtitle}</Card.Title>
                            <Card.Text>{certificate.description}</Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            ))}
            </Row>
        </Container>
    );
}

export default Certificates;
