import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// Function to handle pointer movement
function handlePointerMove(e) {
  const { currentTarget: el, clientX: x, clientY: y } = e;
  const { top: t, left: l, width: w, height: h } = el.getBoundingClientRect();
  el.style.setProperty('--posX', x - l - w / 2);
  el.style.setProperty('--posY', y - t - h / 2);
}

// Check if the device width is less than 768px
function isMobileDevice() {
  return window.innerWidth < 768;
}

// Add event listener for pointer move
if (!isMobileDevice()) {
  document.body.addEventListener("pointermove", handlePointerMove);
} else {
  // If on a mobile device, trigger automatic movement
  setInterval(() => {
    const el = document.body;
    const x = window.innerWidth / 2; // Center of the screen
    const y = window.innerHeight / 2; // Center of the screen
    const { top: t, left: l, width: w, height: h } = el.getBoundingClientRect();
    el.style.setProperty('--posX', x - l - w / 2);
    el.style.setProperty('--posY', y - t - h / 2);
  }, 100); // Adjust speed as needed
}


reportWebVitals();
