import React from 'react';
import Container from 'react-bootstrap/Container';
import pic from '../assets/pic.webp';
import Badge from 'react-bootstrap/Badge';
import Stack from 'react-bootstrap/Stack';
import Table from 'react-bootstrap/Table';

function PictureSection() {
    return (
        <div className="col-12 col-lg-6 px-0 pe-lg-5 d-flex flex-column">
            <img src={pic} className="img-fluid rounded-4 object-fit-cover" alt="Profile"></img>
        </div>
    );
}

function TextSection() {
    return (
        <div className="col-12 col-lg-6 px-0 my-5 d-flex flex-column">
            <h6 className="display-6 fw-bolder underline w-max">About me</h6>
            <p className="lead my-3">
                Hi! I'm Matteo Migliarese, a highly skilled and passionate web developer. My background in computer engineering and my extensive experience in the industry have allowed me to become an expert in creating professional and functional websites. I use the latest technologies and development methodologies to ensure that my projects are efficient, well-structured, and easy for users to navigate. I'm committed to understanding my clients' needs and work closely with them to achieve their goals and meet their expectations. My attention to detail, ability to work efficiently, and dedication to staying up-to-date on the latest news in web development make me an asset to any team or project. I'm always looking for new challenges and am eager to apply my expertise and knowledge to help create successful websites.
            </p>
            <a className='d-block w-max bg-dark text-light fw-bolder rounded-2 text-light py-2 mt-3 px-3 text-decoration-none'>Read more...</a>
        </div>
    );
}

function SkillsSection() {
    return (
        <div className="col-12 mt-5 d-flex flex-column">
            <h6 className="display-6 fw-bolder underline w-max mb-3">Programming skills</h6>
            <p className='lead'>Below, you'll find a comprehensive list of my programming skills spanning both software and web development. Continuously enhancing my expertise, I am dedicated to delivering ongoing improvements.</p>
            <h4>Web developing:</h4>
            <Stack className='lead mt-2 d-flex flex-wrap' direction="horizontal" gap={2}>
                <Badge bg="accent">HTML5</Badge>
                <Badge bg="accent">CSS3</Badge>
                <Badge bg="accent">JavaScript ES6</Badge>
                <Badge bg="accent">PHP</Badge>
                <Badge bg="accent">ReactJS</Badge>
                <Badge bg="accent">Bootstrap 5</Badge>
                <Badge bg="accent">TailWind CSS</Badge>
                <Badge bg="adding">MySQL</Badge>
            </Stack>
            <h4 className='mt-3'>Software developing:</h4>
            <Stack className='lead mt-2 d-flex flex-wrap' direction="horizontal" gap={2}>
                <Badge bg="accent">Java</Badge>
                <Badge bg="accent">Python</Badge>
                <Badge bg="accent">C++</Badge>
                <Badge bg="adding">Kotlin</Badge>
            </Stack>
        </div>
    );
}

function SpeakingSection() {
    const languages = [
        { id: 1, name: 'Italian', level: 'Native' },
        { id: 2, name: 'English', level: 'B2' },
        { id: 3, name: 'French', level: 'A1' },
        { id: 4, name: 'German', level: 'A1' }
    ];

    return (
        <div className="col-12 mt-5 d-flex flex-column">
            <h6 className="display-6 fw-bolder underline w-max mb-3">Spoken languages</h6>
            <p className='lead'>I'm proficient in four languages, my proficiency levels are listed below. <span className='opacity-50'>(*not certified)</span></p>
            <Table striped>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Language</th>
                        <th>Level</th>
                    </tr>
                </thead>
                <tbody>
                    {languages.map(language => (
                        <tr key={language.id}>
                            <td>{language.id}</td>
                            <td>{language.name}</td>
                            <td>{language.level}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}

function MusicSection() {
    const events = [
        { id: 1, location: 'Volpiano (TO)', date: '25/05/2024', repertoire: 'Franz Liszt - Liebestraüm no.3' }
    ];

    return (
        <div className="col-12 mt-5 d-flex flex-column">
            <h6 className="display-6 fw-bolder underline w-max mb-3">Classical piano studies</h6>
            <p className='lead'>I have been studying classical piano for about 1 year at the 'Ludovico Lessona' music institute located in Volpiano (TO). I am working hard to reach a satisfactory level that will allow me to enter the 'Giuseppe Verdi' conservatory in Turin. I plan to take my solfeggio exam in September 2025. Below you can find a table of my upcoming events and the repertoire I will be playing.</p>
            <h4>Upcoming events:</h4>
            <Table striped>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Where</th>
                        <th>Date</th>
                        <th>Repertoire</th>
                    </tr>
                </thead>
                <tbody>
                    {events.map(event => (
                        <tr key={event.id}>
                            <td>{event.id}</td>
                            <td>{event.location}</td>
                            <td>{event.date}</td>
                            <td>{event.repertoire}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}

function AboutLanding({ showMusicSection }) {
    return (
        <Container>
            <div className="row flex-wrap px-3 my-5">
                <PictureSection />
                <TextSection />
                <SkillsSection />
                <SpeakingSection />
                {showMusicSection && <MusicSection />}
            </div>
        </Container>
    );
}

export default AboutLanding;
