function ECommerceSection() {
    return (
        <Container>
            <Row id='certifications' className="px-2 my-5">
            <h6 className="px-0 mx-2 display-6 fw-bolder underline w-max mb-3">Shop</h6>
            <p className='px-0 mx-2 lead'>Over the years, I have taken several exams aimed at earning useful certifications in different fields and you can find some of them below!</p>
            {certificates.map((certificate) => (
                <Col key={certificate.id} lg={3} md={6} className="mb-3 px-2">
                    <Card style={{ height: "100%" }}>
                        <div className='bg-dark text-light py-2 px-3 fw-bold'>{certificate.title}</div>
                        <Card.Body>
                            <Card.Title>{certificate.subtitle}</Card.Title>
                            <Card.Text>{certificate.description}</Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            ))}
            </Row>
        </Container>
    );
}

export default ECommerceSection;
